css:
@import url('https://fonts.googleapis.com/css2?family=Tajawal:wght@400;500;700&display=swap');
body {
    font-family: 'Tajawal', sans-serif;
}
.page1 {
    font-family: 'Tajawal', sans-serif;
    margin-bottom:95%;
}.page3 {
    font-family: 'Tajawal', sans-serif;
   margin-bottom: 10%;
}
table{
    width:98%;
     font-family: 'Tajawal', sans-serif;
}
td{
   text-align: start;
    direction: rtl;
    padding: 5% !important;
     font-family: 'Tajawal', sans-serif;
}
.print-format td, .print-format th {
    padding: 18px !important;
}
h1 {
    font-size: 22px;
    text-align: center;
    font-weight:bold;
     font-family: 'Tajawal', sans-serif;
}

h2 {
    font-size: 18px;
    text-align: center;
     font-family: 'Tajawal', sans-serif;
}

h3 {
    font-size: 18px;
     font-family: 'Tajawal', sans-serif;
}

p {direction: rtl;
    font-size: 16px;
    line-height: 1.55;
     font-family: 'Tajawal', sans-serif;
}

.side-title{
        direction: rtl;
    font-weight: bold;
    font-size: 18px;
    font-family: 'Tajawal', sans-serif;
      text-decoration: underline;
}

.signatures {
    display: flex;
    justify-content: space-between;
     font-family: 'Tajawal', sans-serif;

}

.signature {
    width: 45%;
    text-align: center;
    font-weight: bold;
     font-family: 'Tajawal', sans-serif;
}
